result:
.profile-container {
    padding: 20px;
}

.user-profile-card, .user-content-card {
    margin-bottom: 20px;
    border-radius: 8px;
    overflow: hidden;
}

.user-info-container {
    width: 100%;
    padding: 20px 0;
    display: flex;
    align-items: center;
    justify-content: space-between;
}

.user-info-details {
    display: flex;
    align-items: center;
    width: 100%;
}

.avatar-uploader {
    position: relative;
    cursor: pointer;
    transition: all 0.3s;
}

.avatar-uploader:hover {
    transform: scale(1.05);
}

.user-avatar {
    box-shadow: 0 2px 12px 0 rgba(0, 0, 0, 0.1);
    border: 2px solid #fff;
}

.avatar-hint {
    position: absolute;
    bottom: -5px;
    left: 50%;
    transform: translateX(-50%);
    font-size: 12px;
    color: #909399;
    opacity: 0;
    transition: opacity 0.3s;
}

.avatar-uploader:hover .avatar-hint {
    opacity: 1;
}

.avatar-uploader-icon {
    font-size: 28px;
    color: #8c939d;
    width: 120px;
    height: 120px;
    line-height: 120px;
    text-align: center;
}

.user-info-details-text {
    margin-left: 30px;
    width: calc(100% - 150px);
}

.user-info-details-text-nickname {
    font-size: 26px;
    font-weight: 600;
    margin: 10px 0;
    color: #303133;
}

.user-info-details-text-time {
    font-size: 14px;
    margin-bottom: 16px;
    color: #909399;
    display: flex;
    align-items: center;
}

.user-info-details-text-time i {
    margin-right: 5px;
}

.user-info-details-text-edit {
    display: flex;
    gap: 10px;
}

.edit-form {
    padding: 10px;
}

.edit-form-item {
    margin-bottom: 20px;
}

.edit-tip {
    font-size: 14px;
    margin: 10px 5px;
    color: #606266;
}

.edit-actions {
    margin-top: 15px;
    display: flex;
    justify-content: flex-end;
    gap: 10px;
}

.custom-tabs {
    margin-bottom: 20px;
    border-radius: 4px;
    overflow: hidden;
}

.tab-icon {
    margin-right: 5px;
}

.empty-state {
    padding: 60px 0;
    text-align: center;
    color: #909399;
}

.empty-icon {
    font-size: 60px;
    color: #c0c4cc;
    margin-bottom: 10px;
}

.empty-text {
    font-size: 14px;
}

.idle-container-list {
    min-height: 55vh;
}

.idle-container-list-item {
    border-bottom: 1px solid #eeeeee;
    cursor: pointer;
    transition: background-color 0.3s;
}

.idle-container-list-item:hover {
    background-color: #f5f7fa;
}

.idle-container-list-item:last-child {
    border-bottom: none;
}

.idle-container-list-item-detile {
    height: 120px;
    display: flex;
    align-items: center;
    padding: 10px;
}

.idle-container-list-item-text {
    margin-left: 15px;
    height: 100px;
    max-width: 800px;
    flex: 1;
}

.idle-container-list-title {
    font-weight: 600;
    font-size: 18px;
    overflow: hidden;
    white-space: nowrap;
    text-overflow: ellipsis;
    color: #303133;
}

.idle-container-list-idle-details {
    font-size: 14px;
    color: #606266;
    padding-top: 5px;
    overflow: hidden;
    white-space: nowrap;
    text-overflow: ellipsis;
}

.idle-container-list-idle-time {
    font-size: 13px;
    padding-top: 5px;
    color: #909399;
}

.idle-item-foot {
    width: 100%;
    display: flex;
    justify-content: space-between;
    align-items: center;
}

.idle-prive {
    font-size: 16px;
    padding-top: 5px;
    color: #f56c6c;
    font-weight: 600;
}

.address-container {
    padding: 10px 20px;
}

.address-container-back {
    margin-bottom: 10px;
}

.address-container-add-title {
    font-size: 15px;
    color: #409EFF;
    padding: 10px;
}

.address-container-add-item {
    margin-bottom: 20px;
}

.demonstration {
    color: #666666;
    font-size: 14px;
    padding: 10px;
}

.address-container-add {
    padding: 0 200px;
}

.address-container-list {
    padding: 30px 100px;
}

@media (max-width: 768px) {
    .user-info-details {
        flex-direction: column;
        align-items: center;
        text-align: center;
    }

    .user-info-details-text {
        margin-left: 0;
        margin-top: 20px;
        width: 100%;
    }

    .user-info-details-text-edit {
        justify-content: center;
    }

    .address-container-add {
        padding: 0 20px;
    }

    .address-container-list {
        padding: 20px 10px;
    }
}

.address-card {
    margin-bottom: 20px;
    border-radius: 8px;
    overflow: hidden;
}

.address-header {
    padding: 10px 0;
    font-weight: 600;
}

.new-address-card {
    margin-bottom: 30px;
}

.new-address-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
}

.address-limit-hint {
    font-size: 12px;
    color: #909399;
}

.address-form {
    padding: 10px 0;
}

.address-actions {
    display: flex;
    justify-content: flex-end;
    margin-top: 10px;
}

.address-list-section {
    margin-top: 20px;
}

.address-list-header {
    font-size: 16px;
    font-weight: 600;
    margin-bottom: 20px;
    color: #303133;
    padding-bottom: 10px;
    border-bottom: 1px solid #f0f0f0;
}

.address-count {
    font-size: 13px;
    color: #909399;
    margin-left: 10px;
    font-weight: normal;
}

.empty-address {
    text-align: center;
    padding: 40px 0;
    color: #909399;
}

.empty-address i {
    font-size: 60px;
    color: #c0c4cc;
    margin-bottom: 10px;
}

.address-item {
    margin-bottom: 20px;
    transition: all 0.3s;
}

.address-item:hover {
    transform: translateY(-2px);
    box-shadow: 0 8px 15px rgba(0, 0, 0, 0.1);
}

.default-address {
    border: 1px solid #67c23a;
}

.address-item-header {
    display: flex;
    align-items: center;
    margin-bottom: 15px;
}

.address-name {
    font-weight: 600;
    color: #303133;
    margin-right: 10px;
}

.address-phone {
    color: #606266;
    margin-right: 10px;
}

.address-item-content {
    padding: 10px 0;
    display: flex;
    align-items: flex-start;
}

.address-item-content i {
    color: #409EFF;
    margin-right: 5px;
    font-size: 16px;
    margin-top: 2px;
}

.address-detail {
    color: #606266;
    line-height: 1.5;
    word-break: break-all;
}

.address-item-actions {
    margin-top: 15px;
    display: flex;
    justify-content: flex-end;
    gap: 5px;
}
