    export default {
        name: "IdleGoods",
        data() {
            return {
                mode: 1,
                nowPage: 1,
                total: 0,
                onlineGoods: [],
                OfflineGoods: [],
                findValue: '',
								status: 1
            }
        },
        created() {
            this.getOnlineGoods();
        },
        methods: {
            searchIdle(){
                this.$api.queryIdle({
                    findValue: this.findValue,
                    page: this.nowPage,
                    nums: 8,
										status: this.status
                }).then(res => {
                    console.log(res);
                    if (res.status_code == 1 && res.data.list != null) {
                            if(res.data.list[0].idleStatus == 1){
                                this.onlineGoods = res.data.list;
                                this.total = res.data.count;
														}else {
                                this.OfflineGoods = res.data.list;
                                this.total = res.data.count;
														}
                    } else{
                        this.$message.error(res.msg)
										}
                }).catch(e => {
                    console.log(e)
                })
            },
            handleCurrentChange(val) {
                this.nowPage = val;
                if (this.mode == 1) {
                    this.getOnlineGoods();
                }
                if (this.mode == 2) {
                    this.getOfflineGoods();
                }
            },
            handleSelect(val) {
                if (this.mode !== val) {
                    this.mode = val;
                    if (val == 1) {
                        this.nowPage = 1;
                        this.getOnlineGoods();
                    }
                    if (val == 2) {
                        this.nowPage = 1;
                        this.getOfflineGoods();
                    }
                }
            },
            makeOfflineGoods(i) {
                this.$api.updateGoods({
                    id: this.onlineGoods[i].id,
                    status: 2
                }).then(res => {
                    if (res.status_code == 1) {
                        this.getOnlineGoods();
                    } else {
                        this.$message.error(res.msg)
                    }
                }).catch(e => {
                    console.log(e)
                })
            },
            deleteGoods(i) {
                this.$api.updateGoods({
                    id: this.OfflineGoods[i].id,
                    status: 0
                }).then(res => {
                    if (res.status_code == 1) {
                        this.getOfflineGoods();
                    } else {
                        this.$message.error(res.msg)
                    }

                }).catch(e => {
                    console.log(e)
                })
            },
            getOnlineGoods() {
                this.$api.getGoods({
                    status: 1,
                    page: this.nowPage,
                    nums: 8
                }).then(res => {
                    if (res.status_code == 1) {
                        this.onlineGoods = res.data.list;
                        this.total = res.data.count;
                    } else {
                        this.$message.error(res.msg)
                    }
                }).catch(e => {
                    console.log(e)
                })
            },
            getOfflineGoods() {
                this.$api.getGoods({
                    status: 2,
                    page: this.nowPage,
                    nums: 8
                }).then(res => {
                    if (res.status_code == 1) {
                        this.OfflineGoods = res.data.list;
                        this.total = res.data.count;
                    } else {
                        this.$message.error(res.msg)
                    }
                }).catch(e => {
                    console.log(e)
                })
            }
        }

    }
